import { useSelector } from "react-redux";

const Cart = () => {
    const { entities } = useSelector((state) => state.cart);
    const countTotalPrice = (data) => {
      const result = data.reduce((prev, curr) => {
        return prev + parseInt(curr.price, 10);
      }, 0);
      return result;
    };
    return (
      <>
        <Row>
          <Col>
            <h1>Daftar Pesanan</h1>

            <p>
              Terima kasih, Consequat esse anim nostrud adipisicing tempor ad quis
              do labore cillum incididunt adipisicing velit laboris. Irure
              consequat laborum mollit adipisicing Lorem laborum laborum officia
              officia magna velit id. Mollit dolor eu irure exercitation eu non
              aute esse dolore ex ullamco et consequat.
            </p>

            <Table>
              <thead>
                <tr>
                  <th>Nama</th>
                  <th>Harga</th>
                </tr>
              </thead>
              <tbody>
                {entities.map((item, index) => {
                  return (
                    <tr>
                      <td>{item.title}</td>
                      <td>$ {item.price}</td>
                    </tr>
                  );
                })}
              </tbody>
              <tfoot>
                <tr>
                  <th>Total</th>
                  <th>$ {countTotalPrice(entities)}.00</th>
                </tr>
              </tfoot>
            </Table>

            <h5>Metode pembayaran</h5>
            <p>
              Silahkan transfer ke rekening a.n Abv Def nomor rekening 1234 4222
              4442 2111
            </p>
          </Col>
        </Row>
      </>
    );
  };

  export default Cart;
