import { useSelector } from "react-redux";

const Cart = () => {
    const { entities } = useSelector((state) => state.cart);
    const countTotalPrice = (data) => {
      const result = data.reduce((prev, curr) => {
        return prev + parseInt(curr.price, 10);
      }, 0);
      return result;
    };

    return (
        <>
            <div className="m-10">
                <h1>Daftar Pesanan</h1>
                <table className="w-full text-sm text-left text-gray-500 dark:text-gray-400">
                    <thead className="text-xs text-gray-50 uppercase bg-purple-500">
                    <tr>
                        <th>Nama</th>
                        <th>Harga</th>
                    </tr>
                    </thead>
                    <tbody>
                    {entities.map((item, index) => {
                        return (
                        <tr>
                            <td>{item.title}</td>
                            <td>$ {item.price}</td>
                        </tr>
                        );
                    })}
                    </tbody>
                    <tfoot>
                    <tr>
                        <th>Total</th>
                        <th>$ {countTotalPrice(entities)}.00</th>
                    </tr>
                    </tfoot>
                </table>

                <h5>Metode pembayaran</h5>
                <p>
                    Silahkan transfer ke rekening a.n Abv Def nomor rekening 1234 4222
                    4442 2111
                </p>
            </div>
        </>
    );
  };

  export default Cart;
