import { useParams } from "react-router-dom";

const Payment = () => {
  const { total } = useParams();

  return (
    <>
        <div className="p-5 background-color-content">
            <h1 className="text-xl font-semibold">Lakukan Pembayaran</h1>
            <h1 className="mt-10 text-l font-semibold">Nominal yang harus dibayar :</h1>
            <h3 className="mt-10 text-xl font-semibold flex justify-center">$ {total}</h3>
        </div>
    </>
  );
};

export default Payment;
