import { useEffect } from "react";
import { Container, Image, Col, Row, Button } from "react-bootstrap";
import { useDispatch, useSelector } from "react-redux";
import { useParams } from "react-router-dom";
import { getProduct } from "../../store/product/action";
import { addItem } from "../../store/cart/slice";
import convertToRupiah from "../../utils/convertToRupiah"

const Detail = () => {
  const { id } = useParams();
  const dispatch = useDispatch();
  const { entity, loading } = useSelector((state) => state.product);

  const fetchProduct = async (id) => {
    await dispatch(getProduct(id));
  };

  useEffect(() => {
    fetchProduct(id);
  }, []);

  return (
    <>
      {loading ? (
        "Loading data"
      ) : (
        <div className="background-color-content">
            <div key={entity.id} className="w-full lg:max-w-full lg:flex bg-white border border-gray-200 rounded-lg shadow dark:bg-gray-800 dark:border-gray-700">
                <img className="mt-20 mb-20 w-25 object-cover md:w-40" src={entity.image} alt="" style={{ height: "10rem", width: "10rem" }} />
                <div className="p-4 flex flex-col justify-between leading-normal" style={{ marginLeft: "160px" }}>
                    <div className="mb-2">
                        <div className="mt-10 text-gray-900 font-bold text-l mb-1">{entity.title}</div>
                        <p>{entity.description}</p>
                        <div className="mt-20 text-blue-500 text-l mb-2">{convertToRupiah(entity.price+"", "Rp")}</div>
                        <button onClick={() => dispatch(addItem(entity))} type="button" className="mt-50 mb-50 focus:outline-none text-white bg-green-400 hover:bg-green-500 focus:ring-4 focus:ring-green-300 font-medium rounded-lg text-sm px-5 py-2.5 mr-2 mb-2 dark:focus:ring-yellow-900">+ Cart</button>         
                    </div>

                </div>
            </div>
        </div>
      )}
    </>
  );
};

export default Detail;
